// 复选框有字
<template>
    <div class="border-box">
      <div class="select-title">Based on your ideal vacation you would be most interested in:</div>
      <div class="image-grid">
        <div v-for="(option, index) in vacationOptions" :key="index" class="option-item">
          <img :src="option.imageUrl" alt="Vacation Image" class="vacation-image">
          <el-checkbox v-model="selectedOptions" :label="option.name">{{ option.name }}</el-checkbox>
        </div>
      </div>
      <button class="confirm-button" @click="confirmSelection">Please confirm your selection</button>
    </div>
  </template>
  
  <script>
  export default {
    data() {
      return {
        selectedOptions: [], // 存储用户选择的选项
      vacationOptions: [
        { name: 'City Hotel', imageUrl: require('@/assets/city-hotel.png') },
        { name: 'Mountain View', imageUrl: require('@/assets/mountain-view.png') },
        { name: 'Beach Resort', imageUrl: require('@/assets/beach-resort.png') },
        { name: 'Glamping', imageUrl: require('@/assets/glamping.png') },
        { name: 'Tree House', imageUrl: require('@/assets/tree-house.png') },
        { name: 'Overwater Bungalow', imageUrl: require('@/assets/overwater-bungalow.png') }
      ]
    };
  },
  methods: {
    submitSelection() {
      // 将勾选框选中的内容发送到后端
      this.$axios.post('http://localhost:5000/saveSelection', {
        selectedOptions: this.selectedOptions
      }, { withCredentials: true })
      .then(response => {
        console.log('Selection saved:', response);
        // 跳转到下一页
        this.$router.push({ path: '/rate', 
        query: { rate: JSON.stringify(response.data) } });
      })
      .catch(error => {
        console.error('Error saving selection:', error);
      });
    }
  }
};
  </script>
  
  <style>
  .border-box {
    border: 1px solid #000;
    padding: 20px;
    border-radius: 5px;
    width: 800px;
    height: 600px;
    margin: 120px auto;
    padding: 20px;
    box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.1);
  }

  .select-title {
    font-family: 'Times New Roman', Arial, sans-serif;
    font-size: 30px;
    font-weight: bold;
    margin-bottom: 40px;
    margin-top: 20px;
    text-align: center;
  }
  
  .image-grid {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 20px;
    margin-top: 20px;
  }
  
  .image-container {
    position: relative;
    text-align: center;
  }
  
  .vacation-image {
    width: 100%;
    height: auto;
    border-radius: 5px;
  }
  
  .checkbox {
    position: absolute;
    top: 10px;
    right: 10px;
    transform: scale(2.5);
  }
  
  .confirm-button {
    margin-top: 20px;
    padding: 10px 20px;
    background-color: #fbfefc;
    color: rgb(0, 0, 0);
    border-radius: 5px;
    font-size: 18px;
    cursor: pointer;
    float: right;
  }
  
  .confirm-button:hover {
    background-color: #808480;
  }
  </style>
